import React from 'react';
import { Button } from '../ui/button';
import { Badge } from '../ui/badge';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '../ui/card';
import { Alert, AlertDescription } from '../ui/alert';
import { CheckCircle, XCircle, AlertTriangle, Info, X } from 'lucide-react';
import { colors, typography as typographyTokens, spacing as spacingTokens, components } from '../../styles/designTokens';

// =============================================================================
// 设计系统 - 统一的UI组件样式规范
// 基于 designTokens.ts 的设计令牌
// =============================================================================

// 颜色系统 - 基于设计令牌的Tailwind类名映射 (深色主题)
export const colorClasses = {
  // 主色调 (品牌橙色)
  primary: {
    50: 'bg-orange-50 text-orange-700 border-orange-200',
    100: 'bg-orange-100 text-orange-800 border-orange-300',
    500: 'bg-orange-500 text-white border-orange-500',
    600: 'bg-orange-600 text-white border-orange-600',
  },
  // 成功色
  success: {
    50: 'bg-green-50 text-green-700 border-green-200',
    100: 'bg-green-100 text-green-800 border-green-300',
    500: 'bg-green-500 text-white border-green-500',
    600: 'bg-green-600 text-white border-green-600',
  },
  // 警告色
  warning: {
    50: 'bg-yellow-50 text-yellow-700 border-yellow-200',
    100: 'bg-yellow-100 text-yellow-800 border-yellow-300',
    500: 'bg-yellow-500 text-white border-yellow-500',
    600: 'bg-yellow-600 text-white border-yellow-600',
  },
  // 错误色
  error: {
    50: 'bg-red-50 text-red-700 border-red-200',
    100: 'bg-red-100 text-red-800 border-red-300',
    500: 'bg-red-500 text-white border-red-500',
    600: 'bg-red-600 text-white border-red-600',
  },
  // 中性色 (深色主题)
  gray: {
    50: 'bg-slate-700 text-slate-200 border-slate-600',
    100: 'bg-slate-600 text-slate-100 border-slate-500',
    500: 'bg-slate-500 text-white border-slate-500',
    600: 'bg-slate-600 text-white border-slate-600',
  }
};

// 间距系统 - 移动端优先 (基于设计令牌)
export const spacing = {
  // 容器间距
  container: 'space-y-4 sm:space-y-6 px-2 sm:px-0',
  // 卡片间距
  card: 'space-y-3 sm:space-y-4',
  // 元素间距
  element: 'space-y-2 sm:space-y-3',
  // 按钮组间距
  buttonGroup: 'flex flex-col sm:flex-row gap-2 sm:space-x-2',
  // 网格间距
  grid: 'gap-3 sm:gap-4',
};

// 响应式文本大小 (基于设计令牌)
export const typography = {
  // 标题 - 使用设计令牌的字体大小
  h1: 'text-2xl sm:text-3xl font-bold',
  h2: 'text-xl sm:text-2xl font-bold', 
  h3: 'text-lg sm:text-xl font-semibold',
  h4: 'text-base sm:text-lg font-medium',
  // 正文
  body: 'text-sm sm:text-base',
  small: 'text-xs sm:text-sm',
  // 标签
  label: 'text-xs sm:text-sm font-medium text-gray-500',
};

// 按钮尺寸系统
export const buttonSizes = {
  xs: 'h-6 px-2 text-xs',
  sm: 'h-8 px-3 text-xs sm:text-sm',
  md: 'h-10 px-4 text-sm sm:text-base',
  lg: 'h-12 px-6 text-base sm:text-lg',
  // 移动端全宽按钮
  mobile: 'w-full sm:w-auto h-10 px-4 text-sm sm:text-base',
};

// 图标尺寸系统
export const iconSizes = {
  xs: 'w-3 h-3',
  sm: 'w-3 h-3 sm:w-4 sm:h-4',
  md: 'w-4 h-4 sm:w-5 sm:h-5',
  lg: 'w-5 h-5 sm:w-6 sm:h-6',
  xl: 'w-6 h-6 sm:w-8 sm:h-8',
};

// 卡片样式系统
export const cardStyles = {
  // 基础卡片
  base: 'bg-white rounded-lg shadow-sm border border-gray-200',
  // 带左边框的卡片
  accent: 'bg-white rounded-lg shadow-sm border border-gray-200 border-l-4',
  // 悬停效果
  hover: 'hover:shadow-md transition-shadow duration-200',
  // 内边距
  padding: {
    sm: 'p-2 sm:p-3',
    md: 'p-3 sm:p-4',
    lg: 'p-4 sm:p-6',
  },
  // 头部内边距
  headerPadding: {
    sm: 'pb-2 sm:pb-3',
    md: 'pb-3 sm:pb-4',
    lg: 'pb-4 sm:pb-6',
  }
};

// =============================================================================
// 统一组件 - 深色主题设计
// =============================================================================

// 统一容器组件 - 深色主题
interface DSContainerProps {
  children: React.ReactNode;
  className?: string;
}

export const DSContainer: React.FC<DSContainerProps> = ({
  children,
  className = ''
}) => {
  return (
    <div className={`
      min-h-screen bg-slate-800 text-white
      ${spacing.container}
      ${className}
    `}>
      {children}
    </div>
  );
};

// 统一按钮组件
interface DSButtonProps {
  variant?: 'primary' | 'secondary' | 'success' | 'warning' | 'error' | 'outline';
  size?: 'xs' | 'sm' | 'md' | 'lg' | 'mobile';
  children: React.ReactNode;
  onClick?: () => void;
  disabled?: boolean;
  icon?: React.ReactNode;
  className?: string;
  type?: 'button' | 'submit' | 'reset';
}

export const DSButton: React.FC<DSButtonProps> = ({
  variant = 'primary',
  size = 'md',
  children,
  onClick,
  disabled = false,
  icon,
  className = '',
  type = 'button'
}) => {
  const getVariantClasses = () => {
    switch (variant) {
      case 'primary':
        return 'bg-orange-500 hover:bg-orange-600 active:bg-orange-700 text-white border-orange-500 shadow-lg shadow-orange-500/25';
      case 'secondary':
        return 'bg-slate-600 hover:bg-slate-700 active:bg-slate-800 text-white border-slate-600';
      case 'success':
        return 'bg-green-500 hover:bg-green-600 active:bg-green-700 text-white border-green-500';
      case 'warning':
        return 'bg-yellow-500 hover:bg-yellow-600 active:bg-yellow-700 text-white border-yellow-500';
      case 'error':
        return 'bg-red-500 hover:bg-red-600 active:bg-red-700 text-white border-red-500';
      case 'outline':
        return 'bg-slate-700 hover:bg-slate-600 active:bg-slate-500 text-white border-2 border-slate-500 hover:border-slate-400';
      default:
        return 'bg-orange-500 hover:bg-orange-600 active:bg-orange-700 text-white border-orange-500 shadow-lg shadow-orange-500/25';
    }
  };

  return (
    <button
      type={type}
      onClick={onClick}
      disabled={disabled}
      className={`
        ${buttonSizes[size]}
        ${getVariantClasses()}
        flex items-center justify-center
        rounded-md border
        font-medium
        transition-colors duration-200
        disabled:opacity-50 disabled:cursor-not-allowed
        touch-manipulation
        ${className}
      `}
    >
      {icon && <span className="mr-2">{icon}</span>}
      {children}
    </button>
  );
};

