import React, { useState } from 'react';
import { Input } from './ui/input';
import { Label } from './ui/label';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from './ui/card';
import { User } from '../types';
import { db } from '../utils/supabase/supabaseClients';
import { db as databaseWrapper } from '../utils/api/databaseWrapper';
import { Loader2 } from 'lucide-react';
import { 
  DSContainer,
  DSButton, 
  DSNotification,
  DSFormLayout,
  typography
} from './common/DesignSystem';

interface LoginFormProps {
  onLogin: (user: User) => void;
}

export function LoginForm({ onLogin }: LoginFormProps) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    try {
      console.log('üîê Starting login process...');
      console.log('üë§ Username:', username);
      console.log('üîë Password length:', password.length);

      // First check if Supabase is healthy
      console.log('üîç Checking database health...');
      const isHealthy = await db.isHealthy();
      if (!isHealthy) {
        throw new Error('Database connection failed. Please check your internet connection and try again.');
      }

      console.log('‚úÖ Database health check passed, attempting login...');

      const user = await databaseWrapper.login(username, password);
      console.log('‚úÖ Login successful for user:', user);
      console.log('üîç User object structure:', JSON.stringify(user, null, 2));
      console.log('üîç User role:', user?.role);
      console.log('üîç User token:', user?.token ? 'Present' : 'Missing');
      onLogin(user);
    } catch (error: any) {
      console.error('‚ùå Login error:', error);

      // If login fails and it's admin credentials, try to initialize admin account
      if (username === 'admin' && password === 'admin123' && error.message?.includes('Invalid')) {
        console.log('üîß Login failed for admin, attempting to initialize admin account...');
        try {
          await db.initializeSampleDataIfNeeded();
          console.log('‚úÖ Admin account initialized, retrying login...');

          // Retry login after initialization
          const retryUser = await databaseWrapper.login(username, password);
          console.log('‚úÖ Retry login successful for user:', retryUser);
          onLogin(retryUser);
          return; // Exit early if retry succeeds
        } catch (initError: any) {
          console.error('‚ùå Failed to initialize admin account:', initError);
          setError(`Login failed. Admin initialization error: ${initError.message}`);
          return;
        }
      }

      setError(error.message || 'Login failed');

    } finally {
      setIsLoading(false);
    }
  };

  return (
    <DSContainer className="flex items-center justify-center">
      <div className="max-w-md w-full">
        {/* Error Notification */}
        {error && (
          <div className="mb-6">
            <DSNotification
              type="error"
              title="Authentication Failed"
              message={error}
              onClose={() => setError('')}
              autoClose={false}
            />
          </div>
        )}

        <Card className="bg-slate-700/95 backdrop-blur-sm border-2 border-orange-500/30 shadow-2xl shadow-orange-500/20">
          <CardHeader className="text-center pb-6 sm:pb-8 pt-6 sm:pt-8 px-4 sm:px-8">
            <div className="mb-4">
              <div className="w-12 h-12 sm:w-16 sm:h-16 mx-auto bg-gradient-to-br from-orange-500 to-orange-600 rounded-full flex items-center justify-center shadow-lg shadow-orange-500/25">
                <span className="text-lg sm:text-2xl font-bold text-white">HW</span>
              </div>
            </div>
            <CardTitle className={`${typography.h2} sm:text-3xl font-bold text-white`}>
              Hoe Win Junket Management System
            </CardTitle>
            <CardDescription className={`${typography.body} text-slate-300 mt-3 font-medium`}>
              Professional Gaming Management Platform
            </CardDescription>
          </CardHeader>
          <CardContent className="px-4 sm:px-8 pb-6 sm:pb-8">
            <form onSubmit={handleSubmit}>
              <DSFormLayout columns={1}>
                <div className="space-y-2">
                  <Label htmlFor="username" className={`${typography.label} text-slate-200 font-semibold`}>
                    Username
                  </Label>
                  <Input
                    id="username"
                    type="text"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    placeholder="Enter username"
                    required
                    disabled={isLoading}
                    className="h-10 sm:h-12 border-2 border-slate-200 focus:border-amber-400 focus:ring-amber-400/20 bg-white/80 backdrop-blur-sm"
                  />
                </div>

                <div className="space-y-2">
                  <Label htmlFor="password" className={`${typography.label} text-slate-700 font-semibold`}>
                    Password
                  </Label>
                  <Input
                    id="password"
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Enter password"
                    required
                    disabled={isLoading}
                    className="h-10 sm:h-12 border-2 border-slate-200 focus:border-amber-400 focus:ring-amber-400/20 bg-white/80 backdrop-blur-sm"
                  />
                </div>

                {/* Credentials Help - Only show when there's an invalid credentials error */}
                {error.includes('Invalid credentials') && (
                  <div className="mt-3 p-3 bg-amber-50/80 border border-amber-200 rounded-md">
                    <div className={`${typography.small} font-semibold mb-2 text-amber-800`}>
                      Valid Credentials:
                    </div>
                    <div className={`${typography.small} space-y-1 text-amber-700`}>
                      <div>‚Ä¢ Admin: <code className="bg-amber-200 px-2 py-1 rounded font-mono text-xs">admin / admin123</code></div>
                      <div>‚Ä¢ Agent: <code className="bg-amber-200 px-2 py-1 rounded font-mono text-xs">agent1 / agent123</code></div>
                      <div>‚Ä¢ Staff: <code className="bg-amber-200 px-2 py-1 rounded font-mono text-xs">staff1 / staff123</code></div>
                    </div>
                  </div>
                )}

                <DSButton
                  type="submit"
                  variant="primary"
                  size="mobile"
                  disabled={isLoading}
                  className="h-10 sm:h-12 bg-gradient-to-r from-amber-500 to-amber-600 hover:from-amber-600 hover:to-amber-700 text-white font-semibold shadow-lg shadow-amber-500/25 border-0 transition-all duration-200"
                  icon={isLoading ? <Loader2 className="w-4 h-4 animate-spin" /> : undefined}
                >
                  {isLoading ? 'Authenticating...' : 'Access Dashboard'}
                </DSButton>
              </DSFormLayout>
            </form>

          </CardContent>
        </Card>
      </div>
    </DSContainer>
  );
}
