        try {
          const checkInData = {
            check_in_photo: e.target?.result as string,
            notes: checkInNotes
          };

          const response = await apiClient.staffCheckIn(checkingInStaff.id, checkInData);

          if (!response.success) {
            throw new Error(response.error || 'Failed to check in staff member');
