import React, { useState } from 'react';
import { Button } from './ui/button';
import { Input } from './ui/input';
import { Label } from './ui/label';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from './ui/card';
import { Alert, AlertDescription } from './ui/alert';
import { Badge } from './ui/badge';
import { User } from '../types';
import { db } from '../utils/supabase/supabaseClients';
import { db as databaseWrapper } from '../utils/api/databaseWrapper';
import { Database, Shield, Info, AlertTriangle, CheckCircle, Key, Users, RefreshCw, Bug } from 'lucide-react';

interface LoginFormProps {
  onLogin: (user: User) => void;
}

export function LoginForm({ onLogin }: LoginFormProps) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [showTestCredentials, setShowTestCredentials] = useState(false);
  const [debugMode, setDebugMode] = useState(false);
  const [connectionTest, setConnectionTest] = useState<any>(null);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    try {
      console.log('🔐 Starting login process...');
      console.log('👤 Username:', username);
      console.log('🔑 Password length:', password.length);

      // First check if Supabase is healthy
      console.log('🔍 Checking database health...');
      const isHealthy = await db.isHealthy();
      if (!isHealthy) {
        throw new Error('Database connection failed. Please check your internet connection and try again.');
      }

      console.log('✅ Database health check passed, attempting login...');

      const user = await databaseWrapper.login(username, password);
      console.log('✅ Login successful for user:', user);
      console.log('🔍 User object structure:', JSON.stringify(user, null, 2));
      console.log('🔍 User role:', user?.role);
      console.log('🔍 User token:', user?.token ? 'Present' : 'Missing');
      onLogin(user);
    } catch (error: any) {
      console.error('❌ Login error:', error);

      // If login fails and it's admin credentials, try to initialize admin account
      if (username === 'admin' && password === 'admin123' && error.message?.includes('Invalid')) {
        console.log('🔧 Login failed for admin, attempting to initialize admin account...');
        try {
          await db.initializeSampleDataIfNeeded();
          console.log('✅ Admin account initialized, retrying login...');

          // Retry login after initialization
          const retryUser = await databaseWrapper.login(username, password);
          console.log('✅ Retry login successful for user:', retryUser);
          onLogin(retryUser);
          return; // Exit early if retry succeeds
        } catch (initError: any) {
          console.error('❌ Failed to initialize admin account:', initError);
          setError(`Login failed. Admin initialization error: ${initError.message}`);
          return;
        }
      }

      setError(error.message || 'Login failed');

      // If login fails, run a connection test for debugging
      if (debugMode) {
        try {
          const testResult = await db.testConnection();
          setConnectionTest(testResult);
        } catch (testError) {
          console.error('Connection test failed:', testError);
        }
      }
    } finally {
      setIsLoading(false);
    }
  };

  const fillTestCredentials = (role: string) => {
    switch (role) {
      case 'admin':
        setUsername('admin');
        setPassword('admin123');
        break;
      case 'agent':
        setUsername('agent1');
        setPassword('agent123');
        break;
      case 'staff':
        setUsername('staff1');
        setPassword('staff123');
        break;
    }
    setError('');
  };

  const testDatabaseConnection = async () => {
    setIsLoading(true);
    try {
      const testResult = await db.testConnection();
      setConnectionTest(testResult);

      if (testResult.success) {
        setError('');
        console.log('✅ Database connection test successful');
      } else {
        setError(`Connection test failed: ${testResult.message}`);
        console.error('❌ Database connection test failed:', testResult);
      }
    } catch (error: any) {
      setError(`Connection test error: ${error.message}`);
      console.error('❌ Connection test error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const initializeAdminAccount = async () => {
    setIsLoading(true);
    try {
      console.log('🔧 Manually initializing admin account...');
      await db.initializeSampleDataIfNeeded();
      setError('');
      alert('Admin account initialized successfully! Try logging in with admin/admin123');
    } catch (error: any) {
      setError(`Initialization failed: ${error.message}`);
      console.error('❌ Admin initialization error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-black via-gray-900 to-black py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full">
        <Card className="bg-white border-4 border-yellow-400 shadow-2xl shadow-yellow-400/20">
          <CardHeader className="text-center bg-gradient-to-r from-black to-gray-800 text-white rounded-t-lg border-b-2 border-yellow-400">
            <CardTitle className="text-3xl font-bold text-yellow-400 mb-2">Hoe Win Junket</CardTitle>
            <CardTitle className="text-xl font-semibold text-white">Management System</CardTitle>
            <CardDescription className="text-gray-300 mt-2">
              Professional Gaming Operations Dashboard
            </CardDescription>
          </CardHeader>
          <CardContent className="bg-gradient-to-b from-white to-gray-50 p-8">
            <form onSubmit={handleSubmit} className="space-y-6">
              <div>
                <Label htmlFor="username" className="text-black font-semibold text-sm uppercase tracking-wide">Username</Label>
                <Input
                  id="username"
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  placeholder="Enter username"
                  required
                  disabled={isLoading}
                  className="mt-2 border-2 border-gray-300 focus:border-yellow-400 focus:ring-yellow-400 bg-white text-black font-medium"
                />
              </div>

              <div>
                <Label htmlFor="password" className="text-black font-semibold text-sm uppercase tracking-wide">Password</Label>
                <Input
                  id="password"
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Enter password"
                  required
                  disabled={isLoading}
                  className="mt-2 border-2 border-gray-300 focus:border-yellow-400 focus:ring-yellow-400 bg-white text-black font-medium"
                />
              </div>

              {error && (
                <Alert className="border-2 border-red-500 bg-gradient-to-r from-red-50 to-red-100">
                  <AlertTriangle className="w-5 h-5 text-red-600" />
                  <AlertDescription className="text-red-800 font-medium">
                    <strong className="text-red-900">Access Denied:</strong> {error}
                    {error.includes('Invalid credentials') && (
                      <div className="mt-3 text-sm bg-red-200 p-3 rounded border border-red-300">
                        <div className="text-red-900 font-semibold mb-2">Valid Credentials:</div>
                        <div className="space-y-1 text-red-800">
                          <div>• Admin: <code className="bg-red-300 px-2 py-1 rounded font-mono text-xs">admin / admin123</code></div>
                          <div>• Agent: <code className="bg-red-300 px-2 py-1 rounded font-mono text-xs">agent1 / agent123</code></div>
                          <div>• Staff: <code className="bg-red-300 px-2 py-1 rounded font-mono text-xs">staff1 / staff123</code></div>
                        </div>
                      </div>
                    )}
                  </AlertDescription>
                </Alert>
              )}

              <Button 
                type="submit" 
                className="w-full bg-gradient-to-r from-black via-gray-800 to-black hover:from-gray-800 hover:via-black hover:to-gray-800 text-white font-bold py-3 px-6 border-2 border-yellow-400 shadow-lg shadow-yellow-400/30 transition-all duration-300 hover:shadow-yellow-400/50 disabled:opacity-50 disabled:cursor-not-allowed" 
                disabled={isLoading}
              >
                <span className="text-yellow-400 font-semibold tracking-wide">
                  {isLoading ? 'AUTHENTICATING...' : 'SECURE ACCESS'}
                </span>
              </Button>
            </form>

            {/* Professional Footer */}
            <div className="mt-8 pt-6 border-t-2 border-yellow-400">
              <div className="text-center">
                <div className="text-xs text-gray-600 font-medium tracking-wider uppercase">
                  Authorized Personnel Only
                </div>
                <div className="text-xs text-gray-500 mt-1">
                  Professional Gaming Operations • Secure Access Portal
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}
