import React, { useState, useEffect, useCallback } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, ComposedChart, AreaChart, Area, Line } from 'recharts';
import { TrendingDown, Users, DollarSign, Activity, RefreshCw, Download, BarChart3, Zap, AlertTriangle, Percent, Trophy, ArrowUpCircle, ArrowUpDown } from 'lucide-react';
import { tokenManager } from '../utils/auth/tokenManager';

import { Card, CardContent, CardDescription, CardHeader, CardTitle } from './ui/card';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from './ui/select';
import { Button } from './ui/button';
import { Alert, AlertDescription } from './ui/alert';
import { Badge } from './ui/badge';
import { User } from '../types';

const REAL_TIME_REFRESH_INTERVAL = 30000;

interface ReportsProps {
  user: User;
}

interface TripSharingData {
  trip_id: string;
  total_win_loss: number;
  total_expenses: number;
  total_rolling_commission: number;
  total_buy_in: number;
  total_buy_out: number;
  net_cash_flow: number;
  net_result: number;
  total_agent_share: number;
  company_share: number;
  agent_share_percentage: number;
  company_share_percentage: number;
  total_rolling: number;
}

interface TripData {
  id: string;
  trip_name: string;
  destination: string;
  start_date: string;
  end_date: string;
  status: string;
  sharing?: TripSharingData;
}

interface CustomerData {
  id: string;
  name: string;
  email: string;
  vip_level: string;
  totalRolling: number;
  totalWinLoss: number;
  totalBuyIn: number;
  totalBuyOut: number;
  isActive: boolean;
  rollingPercentage: number;
}

interface AgentData {
  id: string;
  name: string;
  email: string;
  status: string;
  commission_rate: number;
}

export function Reports({ user }: ReportsProps) {
  // Data states
  const [trips, setTrips] = useState<TripData[]>([]);
  const [customers, setCustomers] = useState<CustomerData[]>([]);
  const [agents, setAgents] = useState<AgentData[]>([]);

  // Filter states
  const [dateRange, setDateRange] = useState('30');
  const [selectedAgent, setSelectedAgent] = useState('all');
  const [reportType, setReportType] = useState('overview');

  // Loading and sync states
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [lastSyncTime, setLastSyncTime] = useState<Date | null>(null);
  const [connectionStatus, setConnectionStatus] = useState<'connected' | 'disconnected' | 'error'>('connected');
  const [errorMessage, setErrorMessage] = useState('');
  const [isRealTimeEnabled, setIsRealTimeEnabled] = useState(true);

  // Helper functions
  const safeFormatNumber = (value: number | undefined | null): string => {
    if (value === undefined || value === null || isNaN(value)) {
      return '0';
    }
    return value.toLocaleString();
  };

  const safeNumber = (value: number | undefined | null): number => {
    if (value === undefined || value === null || isNaN(value)) {
      return 0;
    }
    return value;
  };

  // Load data from backend API
  const loadReportsData = useCallback(async () => {
    try {
      setRefreshing(true);
      setErrorMessage('');

      console.log('📊 Loading reports data from backend API...');

      const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001/api';
      const token = await tokenManager.getToken();

      if (!token) {
        throw new Error('No authentication token found. Please log in again.');
      }

      // Load all required data from backend API
      const [tripsResponse, customersResponse, agentsResponse] = await Promise.all([
        fetch(`${API_URL}/trips`, {
          headers: { 'Authorization': `Bearer ${token}` }
        }),
        fetch(`${API_URL}/customers`, {
          headers: { 'Authorization': `Bearer ${token}` }
        }),
        fetch(`${API_URL}/agents`, {
          headers: { 'Authorization': `Bearer ${token}` }
        })
      ]);

      if (!tripsResponse.ok || !customersResponse.ok || !agentsResponse.ok) {
        throw new Error('Failed to fetch data from backend API');
      }

      const [tripsResult, customersResult, agentsResult] = await Promise.all([
        tripsResponse.json(),
        customersResponse.json(),
        agentsResponse.json()
      ]);

      const tripsData = tripsResult.data || [];
      const customersData = customersResult.data || [];
      const agentsData = agentsResult.data || [];

      // Process trips data with sharing information
      const processedTrips = tripsData.map((trip: any) => ({
        ...trip,
        sharing: trip.sharing || {
          total_win_loss: 0,
          total_expenses: 0,
          total_rolling_commission: 0,
          total_buy_in: 0,
          total_buy_out: 0,
          net_cash_flow: 0,
          net_result: 0,
          total_agent_share: 0,
          company_share: 0,
          agent_share_percentage: 0,
          company_share_percentage: 100,
          total_rolling: 0
        }
      }));

      // Process customers data
      const processedCustomers = customersData.map((customer: any) => ({
        ...customer,
        totalRolling: customer.totalRolling || 0,
        totalWinLoss: customer.totalWinLoss || 0,
        totalBuyIn: customer.totalBuyIn || 0,
        totalBuyOut: customer.totalBuyOut || 0,
        isActive: customer.isActive || false,
        rollingPercentage: customer.rollingPercentage || 1.4
      }));

      // Set processed data
      setTrips(processedTrips);
      setCustomers(processedCustomers);
      setAgents(agentsData);
      setLastSyncTime(new Date());
      setConnectionStatus('connected');

      console.log(`✅ Reports data loaded: ${processedCustomers.length} customers, ${agentsData.length} agents, ${processedTrips.length} trips`);

    } catch (error) {
      console.error('❌ Error loading reports data:', error);
      const errorMessage = error instanceof Error ? error.message : 'Unknown error occurred';
      setErrorMessage(`Failed to load reports data: ${errorMessage}`);
      setConnectionStatus('error');
    } finally {
      setLoading(false);
      setRefreshing(false);
    }
  }, []);

  // Real-time data sync
  useEffect(() => {
    loadReportsData();

    let refreshInterval: NodeJS.Timeout;
    if (isRealTimeEnabled) {
      refreshInterval = setInterval(() => {
        console.log('📊 Real-time reports refresh triggered');
        loadReportsData();
      }, REAL_TIME_REFRESH_INTERVAL);
    }

    return () => {
      if (refreshInterval) {
        clearInterval(refreshInterval);
      }
    };
  }, [loadReportsData, isRealTimeEnabled]);

  // Toggle real-time updates
  const toggleRealTime = () => {
    setIsRealTimeEnabled(!isRealTimeEnabled);
    if (!isRealTimeEnabled) {
      loadReportsData();
    }
  };

  // Filter data based on user role and filters
  const getFilteredData = () => {
    let filteredCustomers = customers;
    let filteredTrips = trips;

    // Apply user role filter
    if (user.role === 'agent' && user.agentId) {
      filteredCustomers = customers.filter(c => c.agentId === user.agentId);
      filteredTrips = trips.filter(t => 
        (t.agents && t.agents.some(agent => agent.agentId === user.agentId)) ||
        t.agentId === user.agentId
      );
    } else if (user.role === 'staff' && user.staffId) {
      filteredTrips = trips.filter(t => (t as any).staffId === user.staffId);
      const tripCustomerIds = Array.from(new Set(
        filteredTrips.flatMap(t => t.customers?.map(tc => tc.customerId) || [])
      ));
      filteredCustomers = customers.filter(c => tripCustomerIds.includes(c.id));
    }

    // Apply agent filter (admin only)
    if (user.role === 'admin' && selectedAgent !== 'all') {
      filteredCustomers = filteredCustomers.filter(c => c.agentId === selectedAgent);
      filteredTrips = filteredTrips.filter(t => 
        (t.agents && t.agents.some(agent => agent.agentId === selectedAgent)) ||
        t.agentId === selectedAgent
      );
    }

    // Apply date range filter
    const cutoffDate = new Date();
    cutoffDate.setDate(cutoffDate.getDate() - parseInt(dateRange));

    filteredTrips = filteredTrips.filter(t => {
      const tripDate = t.start_date || t.end_date;
      return tripDate && new Date(tripDate) >= cutoffDate;
    });

    return { filteredCustomers, filteredTrips };
  };

  const { filteredCustomers, filteredTrips } = getFilteredData();

  // Calculate metrics from trip_sharing data
  const calculateMetrics = () => {
    // Main metrics from trip_sharing table
    const totalRolling = filteredTrips.reduce((sum, trip) => sum + safeNumber(trip.sharing?.total_rolling), 0);
    const totalWinLoss = filteredTrips.reduce((sum, trip) => sum + safeNumber(trip.sharing?.total_win_loss), 0);
    const totalRollingCommission = filteredTrips.reduce((sum, trip) => sum + safeNumber(trip.sharing?.total_rolling_commission), 0);
    const companyShare = filteredTrips.reduce((sum, trip) => sum + safeNumber(trip.sharing?.company_share), 0);
    const totalBuyIn = filteredTrips.reduce((sum, trip) => sum + safeNumber(trip.sharing?.total_buy_in), 0);
    const totalBuyOut = filteredTrips.reduce((sum, trip) => sum + safeNumber(trip.sharing?.total_buy_out), 0);
    const totalExpenses = filteredTrips.reduce((sum, trip) => sum + safeNumber(trip.sharing?.total_expenses), 0);

    // Customer metrics
    const totalCustomers = filteredCustomers.length;
    const activeCustomers = filteredCustomers.filter(c => (c.totalRolling || 0) > 0).length;

    // Trip metrics
    const totalTrips = filteredTrips.length;
    const completedTrips = filteredTrips.filter(t => t.status === 'completed').length;
    const ongoingTrips = filteredTrips.filter(t => t.status === 'in-progress').length;

    // Performance ratios
    const isCompanyProfitable = companyShare > 0;
    const profitMargin = totalRolling > 0 ? ((companyShare / totalRolling) * 100) : 0;

    console.group('📊 Calculated Metrics from trip_sharing');
    console.log('🎲 Total Rolling:', totalRolling);
    console.log('💰 Total Win/Loss:', totalWinLoss);
    console.log('💼 Total Rolling Commission:', totalRollingCommission);
    console.log('🏢 Company Share:', companyShare);
    console.log('👥 Active Customers:', activeCustomers);
    console.log('📈 Is Company Profitable:', isCompanyProfitable);
    console.groupEnd();

    return {
      totalRolling,
      totalWinLoss: Math.abs(totalWinLoss),
      totalRollingCommission,
      companyShare,
      totalBuyIn,
      totalBuyOut,
      totalExpenses,
      totalCustomers,
      activeCustomers,
      totalTrips,
      completedTrips,
      ongoingTrips,
      isCompanyProfitable,
      profitMargin,
      netCashFlow: totalBuyOut - totalBuyIn
    };
  };

  const metrics = calculateMetrics();

  // Export functionality
  const handleExport = () => {
    const reportData = {
      generatedAt: new Date().toISOString(),
      dateRange: dateRange,
      selectedAgent: selectedAgent,
      userRole: user.role,
      summary: metrics,
      rawData: {
        customers: filteredCustomers.length,
        trips: filteredTrips.length
      }
    };

    const blob = new Blob([JSON.stringify(reportData, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `casino-reports-${new Date().toISOString().split('T')[0]}.json`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center py-12">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-2 text-sm text-gray-600">Loading reports data...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Real-time Status Header */}
      <div className="p-4 bg-purple-50 border border-purple-200 rounded-lg">
        <div className="flex items-center justify-between">
          <div className="flex items-center">
            <BarChart3 className="w-5 h-5 text-purple-600 mr-3" />
            <div>
              <p className="text-sm font-medium text-purple-800">
                📊 Real-time Reports & Analytics - Live Data from trip_sharing
              </p>
              <p className="text-xs text-purple-600">
                Data sourced from trip_sharing table with accurate financial metrics.
                {lastSyncTime && ` • Last sync: ${lastSyncTime.toLocaleTimeString()}`}
              </p>
            </div>
          </div>
          <div className="flex items-center space-x-3">
            {refreshing && (
              <div className="flex items-center text-blue-600">
                <Activity className="w-4 h-4 mr-1 animate-pulse" />
                <span className="text-xs">Syncing...</span>
              </div>
            )}
            <Button 
              variant="outline" 
              size="sm" 
              onClick={loadReportsData}
              disabled={refreshing}
              className="text-xs"
            >
              <RefreshCw className="w-3 h-3 mr-1" />
              Refresh
            </Button>
            <Button 
              variant="outline" 
              size="sm" 
              onClick={toggleRealTime}
              className="text-xs"
            >
              <Zap className={`w-3 h-3 mr-1 ${isRealTimeEnabled ? 'text-green-500' : 'text-gray-500'}`} />
              {isRealTimeEnabled ? 'Live' : 'Manual'}
            </Button>
            <Badge variant="outline" className={`text-xs ${connectionStatus === 'connected' ? 'bg-green-50 text-green-700' : 'bg-red-50 text-red-700'}`}>
              <div className={`w-2 h-2 rounded-full mr-1 ${connectionStatus === 'connected' ? 'bg-green-500 animate-pulse' : 'bg-red-500'}`}></div>
              {connectionStatus === 'connected' ? 'Live Data' : 'Error'}
            </Badge>
          </div>
        </div>
      </div>

      {/* Error Alert */}
      {errorMessage && (
        <Alert className="border-red-200 bg-red-50">
          <AlertTriangle className="w-4 h-4 text-red-600" />
          <AlertDescription className="text-red-800">
            <strong>Reports Error:</strong> {errorMessage}
            <Button
              onClick={loadReportsData}
              size="sm"
              variant="outline"
              className="ml-3 text-red-800 border-red-300 hover:bg-red-100"
            >
              <RefreshCw className="w-3 h-3 mr-1" />
              Retry
            </Button>
          </AlertDescription>
        </Alert>
      )}

      {/* Header and Filters */}
      <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center gap-4">
        <div>
          <h2 className="text-2xl font-bold">Real-time Reports & Analytics</h2>
          <p className="text-gray-600">
            {user.role === 'agent' ? 'Performance overview for your customers' : 
             user.role === 'staff' ? 'Reports for your recorded transactions' :
             'Comprehensive business analytics'} • Live data from trip_sharing
          </p>
          <p className="text-xs text-gray-500 mt-1">
            Showing data for last {dateRange} days • {metrics.totalTrips} trips • Based on trip_sharing table
          </p>
        </div>
        <div className="flex flex-wrap gap-2">
          <Select value={dateRange} onValueChange={setDateRange}>
            <SelectTrigger className="w-32">
              <SelectValue placeholder="Date range" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="7">Last 7 days</SelectItem>
              <SelectItem value="30">Last 30 days</SelectItem>
              <SelectItem value="90">Last 90 days</SelectItem>
              <SelectItem value="365">Last year</SelectItem>
            </SelectContent>
          </Select>

          {user.role === 'admin' && (
            <Select value={selectedAgent} onValueChange={setSelectedAgent}>
              <SelectTrigger className="w-40">
                <SelectValue placeholder="All agents" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Agents</SelectItem>
                {agents.map((agent) => (
                  <SelectItem key={agent.id} value={agent.id}>
                    {agent.name}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          )}

          <Select value={reportType} onValueChange={setReportType}>
            <SelectTrigger className="w-36">
              <SelectValue placeholder="Report type" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="overview">Overview</SelectItem>
              <SelectItem value="financial">Financial</SelectItem>
              <SelectItem value="customer">Customer</SelectItem>
              <SelectItem value="operational">Operational</SelectItem>
            </SelectContent>
          </Select>

          <Button variant="outline" onClick={handleExport}>
            <Download className="w-4 h-4 mr-2" />
            Export
          </Button>
        </div>
      </div>

      {/* Summary Cards */}
      <div>
        <h3 className="text-lg font-medium mb-4">Key Performance Metrics</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-6">
          <Card className="bg-gradient-to-r from-blue-50 to-blue-100 border-blue-200">
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium text-blue-800">Total Rolling</CardTitle>
              <DollarSign className="h-4 w-4 text-blue-600" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-blue-700">HK${safeFormatNumber(metrics.totalRolling)}</div>
              <p className="text-xs text-blue-600">
                From trip_sharing.total_rolling • Last {dateRange} days
              </p>
            </CardContent>
          </Card>

          <Card className="bg-gradient-to-r from-purple-50 to-purple-100 border-purple-200">
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium text-purple-800">Commission</CardTitle>
              <Percent className="h-4 w-4 text-purple-600" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-purple-700">HK${safeFormatNumber(metrics.totalRollingCommission)}</div>
              <p className="text-xs text-purple-600">
                From trip_sharing.total_rolling_commission
              </p>
            </CardContent>
          </Card>

          <Card className={`bg-gradient-to-r ${metrics.isCompanyProfitable ? 'from-green-50 to-green-100 border-green-200' : 'from-red-50 to-red-100 border-red-200'}`}>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className={`text-sm font-medium ${metrics.isCompanyProfitable ? 'text-green-800' : 'text-red-800'}`}>House P&L</CardTitle>
              {metrics.isCompanyProfitable ? (
                <Trophy className="h-4 w-4 text-green-600" />
              ) : (
                <TrendingDown className="h-4 w-4 text-red-600" />
              )}
            </CardHeader>
            <CardContent>
              <div className={`text-2xl font-bold ${metrics.isCompanyProfitable ? 'text-green-600' : 'text-red-600'}`}>
                HK${safeFormatNumber(Math.abs(metrics.companyShare))}
              </div>
              <p className={`text-xs ${metrics.isCompanyProfitable ? 'text-green-600' : 'text-red-600'}`}>
                {metrics.isCompanyProfitable ? 'Company Profit' : 'Company Loss'} • trip_sharing.company_share
              </p>
            </CardContent>
          </Card>

          <Card className="bg-gradient-to-r from-orange-50 to-orange-100 border-orange-200">
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium text-orange-800">Cash Flow</CardTitle>
              <ArrowUpCircle className="h-4 w-4 text-orange-600" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-orange-700">
                HK${safeFormatNumber(metrics.netCashFlow)}
              </div>
              <p className="text-xs text-orange-600">
                Net: Buy-out - Buy-in
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Active Customers</CardTitle>
              <Users className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{metrics.activeCustomers}</div>
              <p className="text-xs text-muted-foreground">
                With rolling amount • of {metrics.totalCustomers} total
              </p>
            </CardContent>
          </Card>
        </div>
      </div>

      {/* Performance Summary */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        <Card>
          <CardHeader>
            <CardTitle>Performance Summary</CardTitle>
            <CardDescription>Key operational metrics from trip_sharing</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="flex justify-between items-center p-3 bg-blue-50 rounded-lg">
                <span className="text-sm text-blue-800">Total Trips</span>
                <span className="font-bold text-blue-700">{metrics.totalTrips}</span>
              </div>
              <div className="flex justify-between items-center p-3 bg-green-50 rounded-lg">
                <span className="text-sm text-green-800">Completed</span>
                <span className="font-bold text-green-700">{metrics.completedTrips}</span>
              </div>
              <div className="flex justify-between items-center p-3 bg-orange-50 rounded-lg">
                <span className="text-sm text-orange-800">Ongoing</span>
                <span className="font-bold text-orange-700">{metrics.ongoingTrips}</span>
              </div>
              <div className="flex justify-between items-center p-3 bg-purple-50 rounded-lg">
                <span className="text-sm text-purple-800">Profit Margin</span>
                <span className="font-bold text-purple-700">{metrics.profitMargin.toFixed(1)}%</span>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Financial Overview</CardTitle>
            <CardDescription>Revenue and cost breakdown</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="flex items-center space-x-3">
                <DollarSign className="w-5 h-5 text-blue-600" />
                <div>
                  <p className="text-sm font-medium">Total Rolling</p>
                  <p className="text-xl font-bold text-blue-600">HK${safeFormatNumber(metrics.totalRolling)}</p>
                </div>
              </div>
              <div className="flex items-center space-x-3">
                <Percent className="w-5 h-5 text-purple-600" />
                <div>
                  <p className="text-sm font-medium">Commission Earned</p>
                  <p className="text-xl font-bold text-purple-600">HK${safeFormatNumber(metrics.totalRollingCommission)}</p>
                </div>
              </div>
              <div className="flex items-center space-x-3">
                <Trophy className={`w-5 h-5 ${metrics.isCompanyProfitable ? 'text-green-600' : 'text-red-600'}`} />
                <div>
                  <p className="text-sm font-medium">Company Share</p>
                  <p className={`text-xl font-bold ${metrics.isCompanyProfitable ? 'text-green-600' : 'text-red-600'}`}>
                    HK${safeFormatNumber(Math.abs(metrics.companyShare))}
                  </p>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Activity Overview</CardTitle>
            <CardDescription>Customer and transaction summary</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="flex items-center space-x-3">
                <Users className="w-5 h-5 text-blue-600" />
                <div>
                  <p className="text-sm font-medium">Active Customers</p>
                  <p className="text-xl font-bold text-blue-600">{metrics.activeCustomers}</p>
                </div>
              </div>
              <div className="flex items-center space-x-3">
                <ArrowUpDown className="w-5 h-5 text-green-600" />
                <div>
                  <p className="text-sm font-medium">Cash Transactions</p>
                  <p className="text-xl font-bold text-green-600">HK${safeFormatNumber(metrics.totalBuyIn + metrics.totalBuyOut)}</p>
                </div>
              </div>
              <div className="flex items-center space-x-3">
                <Activity className="w-5 h-5 text-purple-600" />
                <div>
                  <p className="text-sm font-medium">Trip Count</p>
                  <p className="text-xl font-bold text-purple-600">{metrics.totalTrips}</p>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}
