import React, { useState } from 'react';
import { Button } from './ui/button';
import { Input } from './ui/input';
import { Label } from './ui/label';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from './ui/card';
import { Alert, AlertDescription } from './ui/alert';
import { Badge } from './ui/badge';
import { User } from '../types';
import { db } from '../utils/supabase/supabaseClients';
import { db as databaseWrapper } from '../utils/api/databaseWrapper';
import { Database, Shield, Info, AlertTriangle, CheckCircle, Key, Users, RefreshCw, Bug, Crown, Diamond, TrendingUp, Lock } from 'lucide-react';

interface LoginFormProps {
  onLogin: (user: User) => void;
}

export function LoginForm({ onLogin }: LoginFormProps) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [showTestCredentials, setShowTestCredentials] = useState(false);
  const [debugMode, setDebugMode] = useState(false);
  const [connectionTest, setConnectionTest] = useState<any>(null);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    try {
      console.log('üîê Starting login process...');
      console.log('üë§ Username:', username);
      console.log('üîë Password length:', password.length);

      // First check if Supabase is healthy
      console.log('üîç Checking database health...');
      const isHealthy = await db.isHealthy();
      if (!isHealthy) {
        throw new Error('Database connection failed. Please check your internet connection and try again.');
      }

      console.log('‚úÖ Database health check passed, attempting login...');

      const user = await databaseWrapper.login(username, password);
      console.log('‚úÖ Login successful for user:', user);
      console.log('üîç User object structure:', JSON.stringify(user, null, 2));
      console.log('üîç User role:', user?.role);
      console.log('üîç User token:', user?.token ? 'Present' : 'Missing');
      onLogin(user);
    } catch (error: any) {
      console.error('‚ùå Login error:', error);

      // If login fails and it's admin credentials, try to initialize admin account
      if (username === 'admin' && password === 'admin123' && error.message?.includes('Invalid')) {
        console.log('üîß Login failed for admin, attempting to initialize admin account...');
        try {
          await db.initializeSampleDataIfNeeded();
          console.log('‚úÖ Admin account initialized, retrying login...');

          // Retry login after initialization
          const retryUser = await databaseWrapper.login(username, password);
          console.log('‚úÖ Retry login successful for user:', retryUser);
          onLogin(retryUser);
          return; // Exit early if retry succeeds
        } catch (initError: any) {
          console.error('‚ùå Failed to initialize admin account:', initError);
          setError(`Login failed. Admin initialization error: ${initError.message}`);
          return;
        }
      }

      setError(error.message || 'Login failed');

      // If login fails, run a connection test for debugging
      if (debugMode) {
        try {
          const testResult = await db.testConnection();
          setConnectionTest(testResult);
        } catch (testError) {
          console.error('Connection test failed:', testError);
        }
      }
    } finally {
      setIsLoading(false);
    }
  };

  const fillTestCredentials = (role: string) => {
    switch (role) {
      case 'admin':
        setUsername('admin');
        setPassword('admin123');
        break;
      case 'agent':
        setUsername('agent1');
        setPassword('agent123');
        break;
      case 'staff':
        setUsername('staff1');
        setPassword('staff123');
        break;
    }
    setError('');
  };

  const testDatabaseConnection = async () => {
    setIsLoading(true);
    try {
      const testResult = await db.testConnection();
      setConnectionTest(testResult);

      if (testResult.success) {
        setError('');
        console.log('‚úÖ Database connection test successful');
      } else {
        setError(`Connection test failed: ${testResult.message}`);
        console.error('‚ùå Database connection test failed:', testResult);
      }
    } catch (error: any) {
      setError(`Connection test error: ${error.message}`);
      console.error('‚ùå Connection test error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const initializeAdminAccount = async () => {
    setIsLoading(true);
    try {
      console.log('üîß Manually initializing admin account...');
      await db.initializeSampleDataIfNeeded();
      setError('');
      alert('Admin account initialized successfully! Try logging in with admin/admin123');
    } catch (error: any) {
      setError(`Initialization failed: ${error.message}`);
      console.error('‚ùå Admin initialization error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 py-12 px-4 sm:px-6 lg:px-8">
      <div className="absolute inset-0 bg-[url('data:image/svg+xml,%3Csvg%20width%3D%2260%22%20height%3D%2260%22%20viewBox%3D%220%200%2060%2060%22%20xmlns%3D%22http%3A//www.w3.org/2000/svg%22%3E%3Cg%20fill%3D%22none%22%20fill-rule%3D%22evenodd%22%3E%3Cg%20fill%3D%22%23ffffff%22%20fill-opacity%3D%220.03%22%3E%3Ccircle%20cx%3D%2230%22%20cy%3D%2230%22%20r%3D%222%22/%3E%3C/g%3E%3C/g%3E%3C/svg%3E')] opacity-20"></div>

      <div className="max-w-md w-full relative z-10">
        {/* Logo and Branding */}
        <div className="text-center mb-8">
          <div className="inline-flex items-center justify-center w-20 h-20 bg-gradient-to-br from-amber-400 via-yellow-500 to-amber-600 rounded-full shadow-2xl mb-4 border-4 border-amber-300/30">
            <Crown className="w-10 h-10 text-white drop-shadow-lg" />
          </div>
          <h1 className="text-4xl font-bold bg-gradient-to-r from-amber-300 via-yellow-400 to-amber-300 bg-clip-text text-transparent mb-2">
            JUNKET ELITE
          </h1>
          <p className="text-amber-200/80 text-sm font-medium tracking-wide">
            Premium Gaming Management Platform
          </p>
        </div>

        <Card className="backdrop-blur-xl bg-white/10 border-white/20 shadow-2xl">
          <CardHeader className="text-center pb-6">
            <div className="flex items-center justify-center space-x-2 mb-3">
              <Diamond className="w-5 h-5 text-amber-400" />
              <CardTitle className="text-xl text-white font-semibold">Management Portal</CardTitle>
              <Diamond className="w-5 h-5 text-amber-400" />
            </div>
            <CardDescription className="text-amber-100/70">
              Secure access to your gaming operations
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-6">
            <form onSubmit={handleSubmit} className="space-y-5">
              <div className="space-y-2">
                <Label htmlFor="username" className="text-amber-100 font-medium flex items-center">
                  <Users className="w-4 h-4 mr-2 text-amber-400" />
                  Username
                </Label>
                <Input
                  id="username"
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  placeholder="Enter your username"
                  required
                  disabled={isLoading}
                  className="bg-white/10 border-white/20 text-white placeholder:text-white/50 focus:border-amber-400 focus:ring-amber-400/20 h-12"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="password" className="text-amber-100 font-medium flex items-center">
                  <Lock className="w-4 h-4 mr-2 text-amber-400" />
                  Password
                </Label>
                <Input
                  id="password"
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Enter your password"
                  required
                  disabled={isLoading}
                  className="bg-white/10 border-white/20 text-white placeholder:text-white/50 focus:border-amber-400 focus:ring-amber-400/20 h-12"
                />
              </div>

              {error && (
                <Alert className="border-red-400/50 bg-red-500/20 backdrop-blur-sm">
                  <AlertTriangle className="w-4 h-4 text-red-400" />
                  <AlertDescription className="text-red-100">
                    <strong>Access Denied:</strong> {error}
                    {error.includes('Invalid credentials') && (
                      <div className="mt-3 p-3 bg-red-500/20 rounded border border-red-400/30">
                        <p className="text-xs text-red-200 mb-2">Valid credentials:</p>
                        <div className="space-y-1 text-xs">
                          <div className="flex justify-between items-center">
                            <span className="text-red-300">Admin:</span>
                            <code className="bg-red-400/20 text-red-200 px-2 py-1 rounded">admin / admin123</code>
                          </div>
                          <div className="flex justify-between items-center">
                            <span className="text-red-300">Agent:</span>
                            <code className="bg-red-400/20 text-red-200 px-2 py-1 rounded">agent1 / agent123</code>
                          </div>
                          <div className="flex justify-between items-center">
                            <span className="text-red-300">Staff:</span>
                            <code className="bg-red-400/20 text-red-200 px-2 py-1 rounded">staff1 / staff123</code>
                          </div>
                        </div>
                      </div>
                    )}
                  </AlertDescription>
                </Alert>
              )}

              <Button 
                type="submit" 
                className="w-full h-12 bg-gradient-to-r from-amber-500 to-yellow-600 hover:from-amber-600 hover:to-yellow-700 text-white font-semibold shadow-lg hover:shadow-xl transition-all duration-200 border-0" 
                disabled={isLoading}
              >
                {isLoading ? (
                  <div className="flex items-center justify-center">
                    <div className="animate-spin rounded-full h-5 w-5 border-2 border-white border-t-transparent mr-2"></div>
                    Authenticating...
                  </div>
                ) : (
                  <div className="flex items-center justify-center">
                    <Shield className="w-5 h-5 mr-2" />
                    Access Portal
                  </div>
                )}
              </Button>
            </form>

            {/* Security Footer */}
            <div className="pt-4 border-t border-white/10">
              <div className="flex items-center justify-center space-x-2 text-xs text-amber-200/60">
                <TrendingUp className="w-3 h-3" />
                <span>Secured by Enterprise-Grade Encryption</span>
                <Shield className="w-3 h-3" />
              </div>
            </div>

          </CardContent>
        </Card>
      </div>
    </div>
  );
}
